/************************************************************************************************************************
 * Project  : Plot graph by getting data through Flask API
 * Author   : Shobhit Gupta
 * Date     : 1st May 2020
 ************************************************************************************************************************/

 // importing the libraries
import React from 'react';
import { render } from 'react-dom';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import axios from 'axios';


const getConfig = data =>({
    chart: {
        type: 'bar'
    },
    title: {
        text: 'Historic World Population by Region'
    },
    subtitle: {
        text: 'Source: <a href="https://en.wikipedia.org/wiki/World_population">Wikipedia.org</a>'
    },
    xAxis: {
        categories: ['Africa', 'America', 'Asia', 'Europe', 'Oceania'], //res.labels,//
        title: {
            text: null
        }
    }, 
    yAxis: {
        min: 0,
        title: {
            text: 'Population (millions)',
            align: 'high'
        },
        labels: {
            overflow: 'justify'
        }
    },
    tooltip: {
        valueSuffix: ' millions'
    },
    plotOptions: {
        bar: {
            dataLabels: {
                enabled: true
            }
        }
    },
    legend: {
        layout: 'vertical',
        align: 'right',
        verticalAlign: 'top',
        x: -40,
        y: 80,
        floating: true,
        borderWidth: 1,
        backgroundColor:
            Highcharts.defaultOptions.legend.backgroundColor || '#FFFFFF',
        shadow: true
    },
    credits: {
        enabled: false
    },
    series: [{
        name: 'Year 1800',
        data: [107, 31, 635, 203, 2] //res.counts//
    }]
})



class App extends React.Component{

    
    constructor(props) {  
        
        super(props);  

        this.state = {
            dataResults: {},
          }  
        
    }

    getData = () => {
        fetch('http://localhost:2010/get_data')
          .then(res => res.json())
          .then(data => {
            this.setState({
              dataResults: data
            });
          });
        }


        componentDidMount() {
            this.getData();
          }  // end of componentMount

    render(){
        const { dataResults } = this.state;
        const chartConfig = getConfig(dataResults);

        return(
            <div>
                <h1> This is a chart</h1>
                <HighchartsReact highcharts={Highcharts} options={chartConfig} />
            </div>
        );
    }

}
