/************************************************************************************************************************
 * Project  : Plot graph by getting data through Flask API
 * Author   : Shobhit Gupta
 * Date     : 1st May 2020
 ************************************************************************************************************************/

 // importing the libraries
import React from 'react';
import { render } from 'react-dom';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import axios from 'axios';

var graph_data = {}

// getting data from API

axios.get(`http://127.0.0.1:2010/get_data`)
      .then(res => {
        graph_data = res.data;
        
      })




// setting up the chart options
const options = {
    chart: {
        type: 'bar'
    },
    title: {
        text: 'Historic World Population by Region'
    },
    subtitle: {
        text: 'Source: <a href="https://en.wikipedia.org/wiki/World_population">Wikipedia.org</a>'
    },
    xAxis: {
        categories: graph_data.labels,//['Africa', 'America', 'Asia', 'Europe', 'Oceania'],
        title: {
            text: null
        }
    }, 
    yAxis: {
        min: 0,
        title: {
            text: 'Population (millions)',
            align: 'high'
        },
        labels: {
            overflow: 'justify'
        }
    },
    tooltip: {
        valueSuffix: ' millions'
    },
    plotOptions: {
        bar: {
            dataLabels: {
                enabled: true
            }
        }
    },
    legend: {
        layout: 'vertical',
        align: 'right',
        verticalAlign: 'top',
        x: -40,
        y: 80,
        floating: true,
        borderWidth: 1,
        backgroundColor:
            Highcharts.defaultOptions.legend.backgroundColor || '#FFFFFF',
        shadow: true
    },
    credits: {
        enabled: false
    },
    series: [{
        name: 'Year 1800',
        data: graph_data.counts//[107, 31, 635, 203, 2]
    }]
};

  const App = () => (
    <div>
      <HighchartsReact highcharts={Highcharts} options={options} />
    </div>
  );

  render(<App />, document.getElementById('root'));
