/************************************************************************************************************************
 * Project  : Plot graph by getting data through Flask API
 * Author   : Shobhit Gupta
 * Date     : 1st May 2020
 ************************************************************************************************************************/

 // importing the libraries
import React from 'react';
import { render } from 'react-dom';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import axios from 'axios';

// setting up the chart options
// const options = {
//     chart: {
//         type: 'bar'
//     },
//     title: {
//         text: 'Historic World Population by Region'
//     },
//     subtitle: {
//         text: 'Source: <a href="https://en.wikipedia.org/wiki/World_population">Wikipedia.org</a>'
//     },
//     xAxis: {
//         categories: graph_data.labels,//['Africa', 'America', 'Asia', 'Europe', 'Oceania'],
//         title: {
//             text: null
//         }
//     }, 
//     yAxis: {
//         min: 0,
//         title: {
//             text: 'Population (millions)',
//             align: 'high'
//         },
//         labels: {
//             overflow: 'justify'
//         }
//     },
//     tooltip: {
//         valueSuffix: ' millions'
//     },
//     plotOptions: {
//         bar: {
//             dataLabels: {
//                 enabled: true
//             }
//         }
//     },
//     legend: {
//         layout: 'vertical',
//         align: 'right',
//         verticalAlign: 'top',
//         x: -40,
//         y: 80,
//         floating: true,
//         borderWidth: 1,
//         backgroundColor:
//             Highcharts.defaultOptions.legend.backgroundColor || '#FFFFFF',
//         shadow: true
//     },
//     credits: {
//         enabled: false
//     },
//     series: [{
//         name: 'Year 1800',
//         data: graph_data.counts//[107, 31, 635, 203, 2]
//     }]
// };



class App extends React.Component{
    constructor(props) {  
        6
                        super(props);  
        7
                        this.state = { Data: {} };  
        8
                }

    componentDidMount(){
        axios.get(`http://127.0.0.1:2010/get_data`).then(res => {
            console.log(res);
            this.setState({
                Data : {
                    chart: {
                        type: 'bar'
                    },
                    title: {
                        text: 'Historic World Population by Region'
                    },
                    subtitle: {
                        text: 'Source: <a href="https://en.wikipedia.org/wiki/World_population">Wikipedia.org</a>'
                    },
                    xAxis: {
                        categories: res.data.labels,//['Africa', 'America', 'Asia', 'Europe', 'Oceania'],
                        title: {
                            text: null
                        }
                    }, 
                    yAxis: {
                        min: 0,
                        title: {
                            text: 'Population (millions)',
                            align: 'high'
                        },
                        labels: {
                            overflow: 'justify'
                        }
                    },
                    tooltip: {
                        valueSuffix: ' millions'
                    },
                    plotOptions: {
                        bar: {
                            dataLabels: {
                                enabled: true
                            }
                        }
                    },
                    legend: {
                        layout: 'vertical',
                        align: 'right',
                        verticalAlign: 'top',
                        x: -40,
                        y: 80,
                        floating: true,
                        borderWidth: 1,
                        backgroundColor:
                            Highcharts.defaultOptions.legend.backgroundColor || '#FFFFFF',
                        shadow: true
                    },
                    credits: {
                        enabled: false
                    },
                    series: [{
                        name: 'Year 1800',
                        data: res.data.counts//[107, 31, 635, 203, 2]
                    }]
                }
            })
        });
    } // end of componentMount

    render(){
        return(
            <div>
                <h1> This is a chart</h1>
                <HighchartsReact highcharts={Highcharts} options={this.state.Data} />
            </div>
        );
    }

}
